import React, { useState, useMemo, useEffect } from 'react';
import PlayerButton from './components/PlayerButton';
import { players } from './data/team-info';
import "./styles/table.css";


function App() {
  const [activePlayers, setActivePlayers] = useState({});
  const [selectedTeam, setSelectedTeam] = useState('');
  const [actionLog, setActionLog] = useState(() => {
    const saved = localStorage.getItem("hofPlayerActionLog");
    return saved ? JSON.parse(saved) : [];
  });

  const teams = useMemo(() => {
    return [...new Set(players.map(player => player.team))];
  }, []);

  // Set default team on initial render and store logs
  useEffect(() => {
    if (teams.length > 0 && !selectedTeam) {
      setSelectedTeam(teams[0]);
    }
  }, [teams, selectedTeam]);
  useEffect(() => {
    localStorage.setItem("hofPlayerActionLog", JSON.stringify(actionLog));
  }, [actionLog]);

  const togglePlayer = (id) => {
    const newStatus = !activePlayers[id];
    const player = players.find(p => p.id === id);

    // Update player status
    setActivePlayers((prev) => ({
      ...prev,
      [id]: newStatus,
    }));

    // Log the toggle action
    const logEntry = {
      playerId: id,
      number: player.number,
      subject: player.subject,
      action: newStatus ? 'ON' : 'OFF',
      time: new Date().toLocaleString('en-US', {
        year: 'numeric',
        month: '2-digit',
        day: '2-digit',
        hour: '2-digit',
        minute: '2-digit',
        second: '2-digit',
      }).replace(/,/, '')
    };

    setActionLog((prev) => [logEntry, ...prev]);
  };

  const handleTeamChange = (e) => {
    setSelectedTeam(e.target.value);
  };

  const teamPlayers = players.filter(p => p.team === selectedTeam);

  const exportToCSV = () => {
    if (actionLog.length === 0) return;

    // CSV header
    const headers = ["jersey_number", "subject", "action", "time"];

    // Convert log to CSV rows
    const rows = actionLog.map(entry => [entry.number, entry.subject, entry.action, entry.time]);

    // Build CSV content
    const csvContent =
      [headers, ...rows]
        .map(row => row.map(field => `"${field}"`).join(","))
        .join("\n");

    // Create Blob and download link
    const blob = new Blob([csvContent], { type: "text/csv;charset=utf-8;" });
    const url = URL.createObjectURL(blob);
    const link = document.createElement("a");
    link.href = url;
    link.setAttribute("download", `player_log_${Date.now()}.csv`);
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  // add functions to upload test logs
  const handleCSVUpload = (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = (event) => {
      const lines = event.target.result.split("\n").slice(1); // skip header
      const entries = lines
        .filter(line => line.trim())
        .map(line => {
          const [number, subject, action, time] = line.split(",");
          return {
            number: number.trim(),
            subject: subject.trim(),
            action: action.trim(),
            time: time.trim(),
            playerId: `${number.trim()}_${time.trim()}`
          };
        });

      setActionLog(prev => [...prev, ...entries]);
    };

    reader.readAsText(file);
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>Team On-Field Tracker</h1>

      <label>
        Select Team:
        <select value={selectedTeam} onChange={handleTeamChange} style={{ marginLeft: '10px' }}>
          {teams.map(team => (
            <option key={team} value={team}>{team}</option>
          ))}
        </select>
      </label>

      <div style={{ marginTop: '20px' }}>
        {teamPlayers.map((player) => (
          <PlayerButton
            key={player.id}
            player={player}
            isActive={!!activePlayers[player.id]}
            onToggle={togglePlayer}
          />
        ))}
      </div>

      <h2 style={{ marginTop: '40px' }}>Player Action Log</h2>
      <input
        type="file"
        accept=".csv"
        onChange={handleCSVUpload}
        style={{ marginTop: '20px' }}
      />
      <button onClick={exportToCSV} style={{ margin: '4px' }}>
        Export to CSV
      </button>
      <button
        onClick={() => {
          setActionLog([]);
          localStorage.removeItem("playerActionLog");
        }}
        style={{ marginLeft: '10px', marginTop: '20px' }}
      >
        Clear Log
      </button>
      <div className="table-container">
        <table border="1" cellPadding="8" style={{ marginTop: '10px', borderCollapse: 'collapse' }}>
          <thead>
            <tr>
              <th>Player #</th>
              <th>Subject ID</th>
              <th>Action</th>
              <th>Time</th>
            </tr>
          </thead>
          <tbody>
            {actionLog.map((entry, idx) => (
              <tr key={idx}>
                <td>{entry.number}</td>
                <td>{entry.subject}</td>
                <td>{entry.action}</td>
                <td>{entry.time}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default App;
