import React, { useState, useMemo, useEffect } from 'react';
import PlayerButton from './components/PlayerButton';
import { players } from './data/team-info';

function App() {
  const [activePlayers, setActivePlayers] = useState({});
  const [selectedTeam, setSelectedTeam] = useState('');
  const [actionLog, setActionLog] = useState([]);
  const teams = useMemo(() => {
    return [...new Set(players.map(player => player.team))];
  }, []);

  // Set default team on initial render
  useEffect(() => {
    if (teams.length > 0 && !selectedTeam) {
      setSelectedTeam(teams[0]);
    }
  }, [teams, selectedTeam]);

  const togglePlayer = (id) => {
    const newStatus = !activePlayers[id];
    const player = players.find(p => p.id === id);

    // Update player status
    setActivePlayers((prev) => ({
      ...prev,
      [id]: newStatus,
    }));

    // Log the toggle action
    const logEntry = {
      playerId: id,
      number: player.number,
      subject: player.subject,
      action: newStatus ? 'ON' : 'OFF',
      time: new Date().toLocaleString(), // human-readable time
    };

    setActionLog((prev) => [...prev, logEntry]);
  };

  const handleTeamChange = (e) => {
    setSelectedTeam(e.target.value);
  };

  const teamPlayers = players.filter(p => p.team === selectedTeam);

  return (
    <div style={{ padding: '20px' }}>
      <h1>Team On-Field Tracker</h1>

      <label>
        Select Team:
        <select value={selectedTeam} onChange={handleTeamChange} style={{ marginLeft: '10px' }}>
          {teams.map(team => (
            <option key={team} value={team}>{team}</option>
          ))}
        </select>
      </label>

      <div style={{ marginTop: '20px' }}>
        {teamPlayers.map((player) => (
          <PlayerButton
            key={player.id}
            player={player}
            isActive={!!activePlayers[player.id]}
            onToggle={togglePlayer}
          />
        ))}
      </div>

      <h2 style={{ marginTop: '40px' }}>Player Action Log</h2>
      <div>
        <table border="1" cellPadding="8" style={{ marginTop: '10px', borderCollapse: 'collapse' }} className="table-container">
          <thead>
            <tr>
              <th>Player #</th>
              <th>Subject ID</th>
              <th>Action</th>
              <th>Time</th>
            </tr>
          </thead>
          <tbody>
            {actionLog.map((entry, idx) => (
              <tr key={idx}>
                <td>{entry.number}</td>
                <td>{entry.subject}</td>
                <td>{entry.action}</td>
                <td>{entry.time}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default App;
