import React from 'react';

export default function PlayerButton({ player, isActive, onToggle }) {
  return (
    <button
      onClick={() => onToggle(player.id)}
      style={{
        padding: '10px 20px',
        margin: '5px',
        backgroundColor: isActive ? 'green' : 'gray',
        color: 'white',
        border: 'none',
        borderRadius: '5px',
        cursor: 'pointer'
      }}
    >
      {player.name}
    </button>
  );
}
